import {createStore} from 'redux'

// 新建store
const ADD_GUN = '加激光枪'
const ADD_DEL = '减激光枪'

// 根据老的state和action 生成新的state
export function counter(state = 0, action) {
    switch (action.type) {
        case ADD_GUN:
            return state + 1
        case ADD_DEL:
            return state - 1
        default:
            return 10
    }
}

export function addGum() {
    return {type: ADD_GUN}
}

export function addDel() {
    return {type: ADD_DEL}
}
